import React, { Component } from 'react';
import style from './App.module.scss';

//components
import RomanNumeralsConverterCard from './atoms/romanNumeralsConverterCard';
import ConversionReferenceChart from './atoms/conversionReferenceChart';

class App extends Component {
  state = {
    formattedRomanNumber: '',
    formattedInteger: ''
  };

  handleChange = () => {

  }
  render() {
    const {
      formattedRomanNumber,
      formattedInteger,
    } = this.state;
    return (
      <div className={style.App}>
        <div className={style.converterCardContainer}>
          <RomanNumeralsConverterCard
            title={'Roman Numerals to Integer'}
            value={formattedInteger}
            onChange={this.handleChange}
          />
          <RomanNumeralsConverterCard
            title={'Integer to Roman Numerals'}
            value={formattedRomanNumber}
            onChange={this.handleChange}
          />
        </div>

        <ConversionReferenceChart />
      </div>
    );
  }
}

export default App;
