import React, { Component } from 'react';
import ConverterCard from '../../atoms/converterCard';

import style from './integerToRomanNumerals.module.scss'

//utils
import toRoman from '../../utils/toRoman';

class IntegerToRomanNumerals extends Component {
    state = {
        formattedRomanNumeral: '',
        errorMessage: ''
    };

    handleChange = (inputValue) => {
        const integer = parseInt(inputValue);
        if (integer === 0 || integer >= 9999) {
            return this.setState({ errorMessage: 'Enter Number between 0 and 999', formattedRomanNumeral: '' })
        }
        const formattedRomanNumeral = toRoman(integer);
        this.setState({ formattedRomanNumeral, errorMessage: '' });
    }

    render() {
        const { formattedRomanNumeral, errorMessage } = this.state;
        return (
            < ConverterCard
                title={'Integer to Roman Numerals'}
                value={formattedRomanNumeral}
                onChange={this.handleChange}
                error={errorMessage}
                className={style.cardContainer}
            />
        );
    }
}

export default IntegerToRomanNumerals;
