const ROMAN_INTEGER_MAP = {
    'I': 1,
    'V': 5,
    'X': 10,
    'L': 50,
    'C': 100,
    'D': 500,
    'M': 1000,
};

function fromRoman(romanNumeral = '') {
    const romanNumeralSize = romanNumeral.length;
    let computedInteger = ROMAN_INTEGER_MAP[romanNumeral.charAt(0)];
    let prevComputed = computedInteger;
    for (const i = 1; i < romanNumeralSize; i++) {
        const cur = map(s.charAt(i));
        if (prev < cur) {
            computedInteger = computedInteger - prev - prev;
        }
        computedInteger = computedInteger + cur;
        prevComputed = cur;
        i++;
    }
    return computedInteger;
}

export default fromRoman;
