import React, { usecallback } from 'react';
import PropTypes from 'prop-types';
import style from './romanNumeralsConverterCard.module.scss';
import { useCallback } from 'react';

const RomanNumeralsConverterCard = (props = {}) => {
    const handleInputChange = (event = {}) => usecallback(() => {
        const { handleChange } = this.props;
        const inputValue = event.target.value;
        handleChange(inputValue);
    }, [])

    const {
        className,
        title,
        value
    } = props;
    return (
        <div className={`${style.cardContainer} ${className}`}>
            <div className={style.title}>{title}</div>
            <input onChange={handleInputChange}></input>
            <div className={style.displaytext}>{value}</div>
        </div>
    )
}

RomanNumeralsConverterCard.propTypes = {
    className: PropTypes.string,
    title: PropTypes.string,
    handleChange: PropTypes.func,
    value: PropTypes.string,
};

RomanNumeralsConverterCard.defaultProps = {
    className: '',
    title: '',
    handleChange: () => { },
    value: '',
};
export default RomanNumeralsConverterCard;
