import React, { Component } from 'react';
import style from './App.module.scss';

//components
import RomanNumeralsToInteger from './molecules/romanNumeralsToInteger';
import IntegerToRomanNumerals from './molecules/integerToRomanNumerals';
import ConversionReferenceChart from './atoms/conversionReferenceChart';

class App extends Component {
  render() {
    const {
      formattedRomanNumber,
      formattedInteger,
    } = this.state;
    return (
      <div className={style.App}>
        <div className={style.converterCardContainer}>
          <div>
            <RomanNumeralsToInteger />
            <IntegerToRomanNumerals
              title={'Integer to Roman Numerals'}
              value={formattedRomanNumber}
              onChange={this.handleChange}
              className={style.integerContainer}
            />
          </div>
          <ConversionReferenceChart />
        </div>
      </div>
    );
  }
}

export default App;
