const INTEGER_LIST = [1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1];

const ROMAN_NUMERAL_LIST = [
    'M',
    'CM',
    'D',
    'CD',
    'C',
    'XC',
    'L',
    'XL',
    'X',
    'IX',
    'V',
    'IV',
    'I',
];

function toRoman(integer) {
    const computedRomanNumeral = '';
    while (integer > 0) {
        const nextHighest = INTEGER_LIST.find(integer);
        const romanNumeral = ROMAN_NUMERAL_LIST[INTEGER_LIST.indexOf(nextHighest)];
        computedRomanNumeral.concat(romanNumeral);
    }
    return computedRomanNumeral;
}

export default toRoman;
